(318, 1, '2024-03-15 20:59:07', '2024-03-15 17:59:07', 'a:13:{s:10:\"aria-label\";s:0:\"\";s:4:\"type\";s:8:\"repeater\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:6:\"layout\";s:5:\"table\";s:10:\"pagination\";i:0;s:3:\"min\";s:0:\"\";s:3:\"max\";s:0:\"\";s:9:\"collapsed\";s:0:\"\";s:12:\"button_label\";s:0:\"\";s:13:\"rows_per_page\";i:20;}', 'Цены и условия договора', 'its-prices', 'publish', 'closed', 'closed', '', 'field_65f48be664dc3', '', '', '2024-03-15 20:59:07', '2024-03-15 17:59:07', '', 266, 'http://vyatka1c.vyatka-it.ru/?post_type=acf-field&p=318', 2, 'acf-field', '', 0),
