(320, 1, '2024-03-15 20:59:07', '2024-03-15 17:59:07', 'a:11:{s:10:\"aria-label\";s:0:\"\";s:4:\"type\";s:4:\"text\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"default_value\";s:0:\"\";s:9:\"maxlength\";s:0:\"\";s:11:\"placeholder\";s:0:\"\";s:7:\"prepend\";s:0:\"\";s:6:\"append\";s:0:\"\";}', 'Текст', 'its-price', 'publish', 'closed', 'closed', '', 'field_65f48c5264dc5', '', '', '2024-03-15 20:59:52', '2024-03-15 17:59:52', '', 318, 'http://vyatka1c.vyatka-it.ru/?post_type=acf-field&#038;p=320', 0, 'acf-field', '', 0);
